import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import './App.css';

import Header from './components/Header';
import NavBar from './components/NavBar';
import SearchForm from './components/SearchForm';
import Advertisement from './components/Advertisement';
import PopularPosts from './components/PopularPosts';
import Footer from './components/Footer';

import Home from './views/Home';
import Programming from './views/Programming';
import Design from './views/Design';
import English from './views/English';
import Software from './views/Software'

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
            categories: [
                {
                    name: 'LẬP TRÌNH',
                    link: '/programming',
                    documents: [
                        {
                            thumnail: 'abc',
                            name: 'Tên document Tên document Tên document Tên document Tên document',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        },
                        {
                            thumnail: 'abc',
                            name: 'TÊN DOCUMENT',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        },
                        {
                            thumnail: 'https://www.wikihow.com/images/2/2b/Convert-a-Word-Document-to-HTML-Step-8.jpg',
                            name: 'TÊN DOCUMENT',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        }
                    ]
                },
                {
                    name: 'THIẾT KẾ - ĐỒ HỌA',
                    link: '/design',
                    documents: [
                        {
                            thumnail: 'abc',
                            name: 'Tên document Tên document Tên document Tên document Tên document',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        },
                        {
                            thumnail: 'abc',
                            name: 'TÊN DOCUMENT',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        },
                        {
                            thumnail: 'https://www.wikihow.com/images/2/2b/Convert-a-Word-Document-to-HTML-Step-8.jpg',
                            name: 'TÊN DOCUMENT',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        }
                    ]
                },
                {
                    name: 'TIẾNG ANH',
                    link: '/english',
                    documents: [
                        {
                            thumnail: 'abc',
                            name: 'Tên document Tên document Tên document Tên document Tên document',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        },
                        {
                            thumnail: 'abc',
                            name: 'TÊN DOCUMENT',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        },
                        {
                            thumnail: 'https://www.wikihow.com/images/2/2b/Convert-a-Word-Document-to-HTML-Step-8.jpg',
                            name: 'TÊN DOCUMENT',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        }
                    ]
                },
                {
                    name: 'PHẦN MỀM',
                    link: '/software',
                    documents: [
                        {
                            thumnail: 'abc',
                            name: 'Tên document Tên document Tên document Tên document Tên document',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        },
                        {
                            thumnail: 'abc',
                            name: 'TÊN DOCUMENT',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        },
                        {
                            thumnail: 'https://www.wikihow.com/images/2/2b/Convert-a-Word-Document-to-HTML-Step-8.jpg',
                            name: 'TÊN DOCUMENT',
                            upload_time: 'Thời gian',
                            comments: 'Bình luận'
                        }
                    ]
                }
            ],
            
        };
    }

    render() {
        // ES6 foreach show all categories
        let categories = this.state.categories;
        let documents = this.state.documents;

        return (
            <Router>
                <div className="App">

                    <div className="container">

                        <Header />

                        <NavBar />

                        <div className="row">
                            {/* <div className="col-sm-8">
                                <Carousel />
                                {elmCategory}
                            </div> */}
                            <Home categories={categories} documents={documents}/>
                            <div className="col-sm-4">
                                <SearchForm />
                                <Advertisement />
                                <PopularPosts />
                            </div>
                        </div>

                        <Footer />
                    </div>

                    <Switch>
                        <Route exact path="/" component={Home} />
                        <Route path="/programming" component={Programming} />
                        <Route path="/design" component={Design}/>
                        <Route path="/english" component={English}/>
                        <Route path="/software" component={Software}/>
                    </Switch>
                </div>
            </Router>
        );
    }
}

export default App;
