import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import './App.css';
import Home from './views/Home';
import Header from './components/Header';
import NavBar from './components/NavBar';
import SearchForm from './components/SearchForm';
import Advertisement from './components/Advertisement';
import PopularPosts from './components/PopularPosts';
import Programming from './views/Programming';
import Footer from './components/Footer';

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
            categories: [
                {
                    name: 'LẬP TRÌNH',
                    link: '/programming',
                },
                {
                    name: 'THIẾT KẾ - ĐỒ HỌA',
                    link: '/design'
                },
                {
                    name: 'TIẾNG ANH',
                    link: '/english'
                },
                {
                    name: 'PHẦN MỀM',
                    link: '/software'
                }
            ],
            documents: [
                {
                    thumnail: '',
                    name: 'Tên document Tên document Tên document Tên document Tên document',
                    upload_time: 'Thời gian',
                    comments: 'Bình luận'
                },
                {
                    thumnail: '',
                    name: 'TÊN DOCUMENT',
                    upload_time: 'Thời gian',
                    comments: 'Bình luận'
                },
                {
                    thumnail: 'https://www.wikihow.com/images/2/2b/Convert-a-Word-Document-to-HTML-Step-8.jpg',
                    name: 'TÊN DOCUMENT',
                    upload_time: 'Thời gian',
                    comments: 'Bình luận'
                }
            ]
        };
    }

    render() {
        // ES6 foreach show all categories
        let categories = this.state.categories;
        console.log(categories);
        let documents = this.state.documents;
        console.log(documents);

        return (
            <Router>
                <div className="App">

                    <div className="container">

                        <Header />

                        <NavBar />

                        <div className="row">
                            {/* <div className="col-sm-8">
                                <Carousel />
                                {elmCategory}
                            </div> */}
                            <Home categories={categories} documents={documents}/>
                            <div className="col-sm-4">
                                <SearchForm />
                                <Advertisement />
                                <PopularPosts />
                            </div>
                        </div>

                        <Footer />
                    </div>

                    <Switch>
                        <Route exact path="/" component={Home} />
                        <Route path="/programming" component={Programming} />
                    </Switch>
                </div>
            </Router>
        );
    }
}

export default App;
