import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import './App.css';
// import Home from './views/Home';
import Header from './components/Header';
import NavBar from './components/NavBar';
import Carousel from './components/Carousel';
import SearchForm from './components/SearchForm';
import Advertisement from './components/Advertisement';
import Category from './components/Category';
import PopularPosts from './components/PopularPosts';
import Footer from './components/Footer';

import Home from './views/Home';
import Programming from './views/Programming';
import GraphicDesign from './views/GraphicDesign';
import English from './views/English';
import Contact from './views/Contact';
import NotFound from './views/NotFound';

import routes from './routes';
import Software from './views/Software';

class App extends Component {
    render() {
        // ES6 foreach show all categories
        //let categories = this.state.categories;
        //let items = this.state.items;

        //let elmCategory = categories.map((item, index) => {
        //    return(
        //        <Category key={index} name={item.name} link={item.link} items={items}/>
        //    );
        //});

        return (
            <Router>
                <div className="App">

                    <div className="container">

                        <Header />

                        <NavBar />

                        <div className="row">
                            <Home/>

                            <div className="col-sm-4">
                                <SearchForm />
                                <Advertisement />
                                <PopularPosts />
                            </div>
                        </div>

                        <Footer />
                    </div>

                    <Switch>
                        {/* <Route exact path="/" component={Home} /> */}
                        <Route path="/programming" component={Programming} />
                        <Route path="/graphicDesign" component={GraphicDesign}/>
                        <Route path="/english" component={English} />
                        <Route path="/software" component={Software}/>
                        <Route path="/contact" component={Contact} />
                        <Route component={NotFound} />
                    </Switch>
                </div>
            </Router>
        );
    }
}

export default App;
